        <script type="text/javascript">
		    var u = new tvs.Annotator();
            u.annotateDocument();
            var pElem2 = $('#p2')[0];
            var $select2 = $('<select></select>'),
                $body = $(document.body);

            $body.append($select2);
            for (var t in u.getTemplates()) {
                $select2.append($('<option></option>').text(t));
            }

            function createCtrl2(title, clickHandler) {
                var $button = $('<button>' + title + '</button>');

                $body.append($button);
                $button.on('click', function() {
                    clickHandler.apply(u, [$select2.find(":selected").text(), 'red']);
                });
            };

            $body.on('click', '.tvs-annotated-text, .tvs-annotate-element', function(e) {
                u.unannotateElement(this);
            });
            
            createCtrl2('underline2', u.underlineSelected);
            createCtrl2('highlight2', u.highlightSelected);
            createCtrl2('strike2', u.strikeSelected);
        </script>

